import Restaurant from "../types/Restaurant";
import {restaurantsActions} from "./restaurants-slice"
import {notificationActions} from "./notification-slice"
export const postUser=(user:any)=>{
    return async (dispatch:any) =>{
        dispatch(notificationActions.showNotification({
             status:'pending',
             title:'check',
             message: 'check user',
        }))

        const postData =async()=>{
            const response= await fetch('http://localhost:3005/auth/',{
                method: 'POST',
                body: JSON.stringify(user),
              });            
              if(!response.ok){
                throw new Error('not able to get data!!! '); 
              }
        }
        try{
            await postData();
            dispatch(notificationActions.showNotification({
                status:'success',
                title:'success',
                message: 'success to add  new restaurant',
           }))
        } catch(error){
            dispatch(notificationActions.showNotification({
                status:'error',
                title:'error',
                message: 'failed to add restaruant',
              }))
        }
    } 
}
