import React, { useEffect, useRef, useState } from "react";
import './AddRestaurant.scss';
import {useDispatch,useSelector} from 'react-redux'

function AddRestaurant() {
    const dispatch=useDispatch();
    const chefs=useSelector((state:any) => state.chefs.chefs);
    const [isValidForm,setIsValidForm]=useState(false); 
    const name_input=useRef<any>('');
    const [name,setName]=useState('');
    const chef_input=useRef(null);
    const dishes_input=useRef(null);
    const signture_dish_input=useRef(null);
    const submitHandler = (event: any) =>{
        event.preventDefault();
            // dispatch(addUser({email:email,password:password}));
    }
    return (
    <form onSubmit={submitHandler} className="login-form">
    <div className='form-control1'>
        <label htmlFor='name'>Name:  </label><br/>
        <input value={name} ref={name_input} className='input' type="text" id="name"/>
    </div>

    <div className="chef-drop-down">
        <label htmlFor='name'>Chef:  </label><br/>
        <select className="drop-down">
        <option> </option>
        {chefs.map((chef:any) => (
            <option
            key={chef.id}
            value={chef.id}
            >
            {chef.name}
            </option>
        ))}
        </select>
    </div>

    <div className="dishes-frame">Dishes:

    </div>
    <div className='form-action1'>
        <button disabled={!isValidForm} className='btn'>Submit</button>
    </div>
    </form>
  )
}
