import {createSlice,configureStore} from '@reduxjs/toolkit'

const initRestaurantsState = {restaurnats:[]}

const restaurantsSlice=createSlice({
    name:'resturants',
    initialState: initRestaurantsState,
    reducers:{
        getRestaurants(state){},
        getRestaurantByID(){},
        postRestaurant(){}
    }
})

const initAuthState = {isAuth:false}

const  authSlice=createSlice({
    name:'auth',
    initialState: initAuthState,
    reducers:{
        login(state){
            state.isAuth=true;
        },
        logout(state){
            state.isAuth=false;
        }
    }
})


const store=configureStore({
    reducer: {restaurant:restaurantsSlice.reducer,auth: authSlice.reducer},
});

export const restaurantsActions= restaurantsSlice.actions;  
export const authActions= authSlice.actions;  
export default store;
