import React,{useState,useEffect} from 'react';
import './DishCard.scss';
import Modal from "react-modal";
import DishModal from '../../Main/Restaurants/Dishes/Dish-Modal/DishModal';

const DishCard:React.FC<{name: string; price:number; src: string; type:string; description:string}>=(props)=> {
  const path='./../images/icons/dishes-types/'+ props.type +'.png'
  const [modalIsOpen,setModalIsOpen] = useState(false);

  const handleAfterOpen =()=>{
      setModalIsOpen(true)
  }

  const handleCloseModal =()=>{
      setModalIsOpen(false)
      console.log("close ",modalIsOpen);
  }

    function toggleModal() {
      this.setState({ modalIsOpen: !this.state.modalIsOpen });
  }

  // useEffect(()=>{
  //   console.log("modal ",modalIsOpen);
  // },[setModalIsOpen])
  return (
    <div onClick={toggleModal} className="dish-card-frame">
        {modalIsOpen &&  <div className='CHECK'>{modalIsOpen}</div>}
      <img className="dish-card-img" src={`./../images/dishes/${props.src}`}/>
      <div className="dish-card-text">
        <span className="dish-name">{props.name}</span><br/>
        <div className="dish-description">{props.description}</div><br/>
          {props.type!=='' ? <img className="dish-icon" src={path}/>: <div className='dish-icon'></div>}
        <div className="dish-price"><hr/>&#8362;{props.price}<hr/></div>
      </div>
      <section>
            {/* <section  className="modal-header">  */}
            <button onClick={toggleModal}>x</button>
            {/* </section> */}
            <DishModal IsModalOpened={modalIsOpen} onCloseModal={handleCloseModal} onAfterOpen={handleAfterOpen} type={props.type} description={props.description} name={props.name} price={props.price} src={props.src}/>
        </section>
    </div>  
  );
}

export default DishCard;
