import React,{useEffect} from 'react';
import Footer from './components/Footer/Footer';
import Header from './components/Header/Header';
import { Route,Redirect,Switch } from "react-router-dom";
import './Epicure.scss';
import Chefs from './components/Main/Chefs/Chefs';
import Home from './components/Main/Home/Home';
import Restaurants from './components/Main/Restaurants/Restaurants';
import Dishes from './components/Main/Restaurants/Dishes/Dishes';
import Auth from './auth/auth';
import {useSelector,useDispatch} from 'react-redux';
import {notificationActions} from './store/notification-slice';
function Epicure() {
  const auth=useSelector((state:any) => state.auth.isAuth);
  const restaurants=useSelector((state:any)=> state.restaurnts.restaurants);
  const dispatch=useDispatch();
  useEffect(()=>{
    const addProduct =async ()=>{  
      dispatch(notificationActions.showNotification({
         status:'pending',
         title:'adding',
         message: 'addin new restaurant',
      }))
      const response= await fetch('http://localhost:3005/restaurants',{
        method: 'POST',
        body: JSON.stringify(restaurants),
      });
      if(!response.ok){
        // dispatch() 
        dispatch(notificationActions.showNotification({
          status:'errror',
          title:'error',
          message: 'failed to add restaruant ',
        }))
      }
      dispatch(notificationActions.showNotification({
        status:'success',
        title:'success',
        message: 'success successfuly',
      }))
    }
  },[restaurants])
  return (
    <div className="Epicure">
      <Header/>
      <main>
        <Switch>
          <Route path="/" exact><Redirect to="/auth"/></Route>
          <Route path="/auth"><Auth/></Route>
          {auth &&<section><Route path="/home"><Home/></Route>
          <Route path="/restaurants" exact><Restaurants/></Route>
          <Route path="/restaurants/:restaurantId"><Dishes/></Route>
          <Route path="/chefs"><Chefs/></Route></section>}
          <Route path="/*" exact><Redirect to="/auth"/></Route>
        </Switch>
      </main>
      <Footer/>
    </div>
  );
}

export default Epicure
