import {createSlice} from '@reduxjs/toolkit'
import Restaurant from '../types/Restaurant';

const initRestaurantsState = {restaurnats:[],restaurant:Restaurant={}}

const restaurantsSlice=createSlice({
    name:'resturants',
    initialState: initRestaurantsState,
    reducers:{
        getRestaurants(state,action){
            state.restaurnats=action.payload.restaurants
        },
    }
})

export const restaurantsActions= restaurantsSlice.actions;
export default restaurantsSlice.reducer;
