import React,{useEffect,useState,useCallback} from 'react';
import './Restaurants.scss';
import restaurantsJson from '../../../../assets/data/restaurants.json';
import chefsJson from '../../../../assets/data/chefs.json';
import dishesJson from '../../../../assets/data/dishes.json';
import Resturant from '../../../types/Resturant';
import Chef from '../../../types/Chef';
import Dish from '../../../types/Dish';
import RestaurantCard from '../../Shared/Cards/RestaurantCard';

function Resturants() {
  // const [restaurants,setRestaurants]=useState<Resturant[]>([]);
  // const [chefs,setChefs]=useState<Chef[]>([]);
  const [popularRestaurants,setpopularRestaurants]=useState<{name:string,chef:string}[]>([]);
  useEffect(()=> {
    const restaurants=restaurantsJson.filter(item=>{
      return item.popular===true;
    });

    restaurants.map(item=>{
      for(let chef of chefsJson){
        if (chef.id===item.chef_id){
          popularRestaurants.push({name:item.name,chef:chef.name});
        }  
      }
    });
    setpopularRestaurants(popularRestaurants);
    console.log(popularRestaurants);
  },[]);

  return (
    <section>
      <span className="popular-title Text-Style-9">
        THE POPULAR RESTAURANTS IN EPICURE:
      </span>
      <div className="cards">
      {popularRestaurants.length>0 && popularRestaurants.map((item,index) => {
          return(
          <RestaurantCard key={index} name={item.name} chef={item.chef} />
            // <div key={index}>
          //   {item.name}<br/>
          //   {item.chef}
          // </div>
          )
        })}
      </div>
    </section>
  );
}

export default Resturants
