import Restaurant from "../types/Restaurant";
import {restaurantsActions} from "./restaurants-slice"
import {notificationActions} from "./notification-slice"
import { userActions } from "./users-slice";
export const getUser=(cradentials:any)=>{
    return async (dispatch:any) =>{
        dispatch(notificationActions.showNotification({
             status:'pending',
             title:'check',
             message: 'check user',
        }))
        console.log("user front",cradentials);
        const getData =async()=>{
            const response= await fetch(`http://localhost:3005/auth/:${cradentials.email}:${cradentials.password}`);
            if(!response.ok){
            throw new Error('not able to get data!!! '); 
            }
        }
        try{
            const user=await getData();
            dispatch(userActions.getUser(user));
        } catch(error){
            dispatch(notificationActions.showNotification({
                status:'error',
                title:'error',
                message: 'failed to find user',
              }))
        }
    } 
}
