    <span className="title Text-Style-9">
      SIGNTURE DISH OF:
    </span>
    <div className="dishes-cards">
      {signtureDish.length > 0 &&
        signtureDish.map((item, index) => {
          return (
            <section>
            <DishCard key={item.id} name={item.name} description={item.description} src={item.src} price={item.price} type={item.type}/>
            </section>
          );
        })}
    </div>
  </section>
  );
}

export default Dishes
