import React, { useEffect, useState } from "react";
import "./ChefOfWeek/ChefOfWeek.scss";
import restaurantsJson from "../../../../../assets/data/restaurants.json";
import chefsJson from "../../../../../assets/data/chefs.json";
import Chef from "../../../../types/Chef";
//./src/Epicure/components/Main/Home/ChefOfWeek
function ChefOfWeek() {
  const [chefOfWeek, setChefOfWeek] = useState<Chef>();
  useEffect(() => {
    const chef_of_week = chefsJson.filter((item) => {
      return item.chefOfW === true;
    });
    setChefOfWeek(chef_of_week);
    for (let chef of chefsJson) {
      if (chef.id === item.chef_id) {
        arr.push({ name: item.name, chef: chef.name, src: item.src });
      }
    }

    setpopularRestaurants(arr);
    // console.log(popularRestaurants);
  }, []);

  return (
    <div className="chef-of-week">
      <span className="chef-title">CHEF OF THE WEEK :</span>
      <br />
      <img className="chef-img" src="./images/chefs/chef_of_week.png" />
      <span className="chef-description">
        Chef Yossi Shitrit has been living and breathing his culinary dreams for
        more than two decades, including running the kitchen in his first
        restaurant, the fondly-remembered Violet, located in Moshav Udim.
        Shitrit's creativity and culinary acumen born of long experience are
        expressed in the every detail of each and every dish.
      </span>
      <div className="card-res-chef">
        <div className="card-res-chef-frame">
          <img
            className="card-res-chef-img"
            src="./images/restaurants/res1/res1.png"
          />
          <span className="card-res-chef-text">Onza</span>
        </div>
        <div className="card-res-chef-frame">
          <img
            className="card-res-chef-img"
            src="./images/restaurants/res2/res2.png"
          />
          <span className="card-res-chef-text">K</span>
        </div>
        <div className="card-res-chef-frame">
          <img
            className="card-res-chef-img"
            src="./images/restaurants/res3/res3.png"
          />
          <span className="card-res-chef-text">cehck checl</span>
        </div>
      </div>
    </div>
  );
}

export default ChefOfWeek;
