import React, { useEffect, useState } from "react";
import './Restaurants.scss';
import restaurantsJson from "../../../../assets/data/restaurants.json";
import chefsJson from "../../../../assets/data/chefs.json";
import RestaurantCard from "../../Shared/Cards/RestaurantCard";
import ChefOfWeek from "../Home/ChefOfWeek/ChefOfWeek";
import Chef from "../../../types/Chef";
import Restaurant from "../../../types/Restaurant";
// import {restaurantsActions } from '../../../store/store' 
import {useDispatch,useSelector} from 'react-redux'

function Restaurants() {
  const dispatch=useDispatch();

  const [restaurants, setRestaurants] = useState<{ id:number,name: string, chef: string,src:string }[]>([]);
  const [chefs, setChefs] = useState< Chef []>([]);
  const res=useSelector((state:any) => state.restaurants.restaurants);
  const chef=useSelector((state:any) => state.chefs.chefs);
  // restaurants

  useEffect(() => {
    res.map((item:any) => {
      for (let ch of chef) {
        if (ch.id === item.chef_id) {
          setRestaurants((currentArray:any) => [...currentArray, { id:item.id,name: item.name, chef: ch.name,src:item.src }])
        }
      }
    });
  },[]);

  return (
  <section>
    <button oc>Add Restaurant</button>
    <div className="cards">
      {restaurants.length > 0 &&
        restaurants.map((item, index) => {
          return (
            <section key={index}>
              <RestaurantCard  name={item.name} chef={item.chef} src={item.src} id={item.id} />
            </section>
          );
        })}
    </div>
  </section>
  )
}
export default Restaurants
