import React,{useEffect} from 'react';
import Footer from './components/Footer/Footer';
import Header from './components/Header/Header';
import { Route,Redirect,Switch } from "react-router-dom";
import './Epicure.scss';
import Chefs from './components/Main/Chefs/Chefs';
import Home from './components/Main/Home/Home';
import Restaurants from './components/Main/Restaurants/Restaurants';
import Dishes from './components/Main/Restaurants/Dishes/Dishes';
import Auth from './auth/auth';
import {useSelector,useDispatch} from 'react-redux';
import {notificationActions} from './store/notification-slice';
import Notification from './components/Shared/Notification/Notification';
import { postRestaurantsData, getRestaurantsData } from './store/restuarants.actions';

let isInitial=true;
function Epicure() {
  const auth=useSelector((state:any) => state.auth.isAuth);
  const restaurant=useSelector((state:any)=> state.restaurants.restaurant);
  const notification=useSelector((state:any)=> state.notification.notification );
  const dispatch=useDispatch();
  //get restaurants
  useEffect(()=>{
    dispatch(getRestaurantsData);
  },[dispatch])

  //post restaurnats
  useEffect(()=>{
    dispatch(notificationActions.showNotification({
      status:'pending',
      title:'adding',
      message: 'addin new restaurant',
 }))
 const postData =async()=>{
     const response= await fetch('http://localhost:3005/restaurants/postRestaurant',{
         method: 'POST',
         body: JSON.stringify(restaurant),
       });            
       if(!response.ok){
         throw new Error('not able to get data!!! '); 
       }
     const data=await response.json;
     return data;
 }
 try{
     postData();
     dispatch(notificationActions.showNotification({
         status:'success',
         title:'success',
         message: 'success to add  new restaurant',
    }))
 } catch(error){
     dispatch(notificationActions.showNotification({
         status:'error',
         title:'error',
         message: 'failed to add restaruant',
       }))
 }
    // if(isInitial){
    //   isInitial=false;
    //   return;
    // }
    // dispatch(postRestaurantsData(restaurant));
  },[restaurant,dispatch])


  return (
    <div className="Epicure">
      <Header/>
      <main>
        {notification && <Notification status={notification.status} title={notification.title} message={notification.message}/>}
        <Switch>
          <Route path="/" exact><Redirect to="/auth"/></Route>
          <Route path="/auth"><Auth/></Route>
          {auth &&<section><Route path="/home"><Home/></Route>
          <Route path="/restaurants" exact><Restaurants/></Route>
          <Route path="/restaurants/:restaurantId"><Dishes/></Route>
          <Route path="/chefs"><Chefs/></Route></section>}
          <Route path="/*" exact><Redirect to="/auth"/></Route>
        </Switch>
      </main>
      <Footer/>
    </div>
  );
}

export default Epicure
